    b = 0;
c = 1;
if (thisComp.layer('text3').text.sourceText == 0)
    c = 0;
d = 1;
if (thisComp.layer('text4').text.sourceText == 0)
    d = 0;
e = 1;
if (thisComp.layer('text5').text.sourceText == 0)
    e = 0;
f = 1;
if (thisComp.layer('text6').text.sourceText == 0)
    f = 0;
$bm_rt = [a + b + c + d + e + f - 1];

p.x:
