s.x:
var $bm_rt;
var a, b, c, d, e, f, x, w, resize;
a = thisComp.layer('text1').sourceRectAtTime() ? thisComp.layer('text1').sourceRectAtTime().width : 0;
b = thisComp.layer('text2').sourceRectAtTime() ? thisComp.layer('text2').sourceRectAtTime().width : 0;
c = thisComp.layer('text3').sourceRectAtTime() ? thisComp.layer('text3').sourceRectAtTime().width : 0;
d = thisComp.layer('text4').sourceRectAtTime() ? thisComp.layer('text4').sourceRectAtTime().width : 0;
e = thisComp.layer('text5').sourceRectAtTime() ? thisComp.layer('text5').sourceRectAtTime().width : 0;
f = thisComp.layer('text6').sourceRectAtTime() ? thisComp.layer('text6').sourceRectAtTime().width : 0;
x = Math.max(a, b, c, d, e, f);
w = 600;
$bm_rt = resize = mul(div(w, x), 100);
if (x > w) {
    $bm_rt = [
        resize,
        resize
    ];
} else {
    $bm_rt = [
        100,
        100
    ];
}

