s.x:
var $bm_rt;
var a, b, c, d, e, f, x, t, w, hs, ws, resize;
a = thisComp.layer('text1').sourceRectAtTime(2) ? thisComp.layer('text1').sourceRectAtTime(2).width : 0;
b = thisComp.layer('text2').sourceRectAtTime(2) ? thisComp.layer('text2').sourceRectAtTime(2).width : 0;
c = thisComp.layer('text3').sourceRectAtTime(2) ? thisComp.layer('text3').sourceRectAtTime(2).width : 0;
d = thisComp.layer('text4').sourceRectAtTime(2) ? thisComp.layer('text4').sourceRectAtTime(2).width : 0;
e = thisComp.layer('text5').sourceRectAtTime(2) ? thisComp.layer('text5').sourceRectAtTime(2).width : 0;
f = thisComp.layer('text6').sourceRectAtTime(2) ? thisComp.layer('text6').sourceRectAtTime(2).width : 0;
x = Math.max(a, b, c, d, e, f);
t = effect('Slider Control')('Slider');
w = 600;
hs = $bm_div(600, $bm_sum(t, 1));
ws = $bm_mul($bm_div(w, x), 100);
resize = Math.min(hs, ws);
$bm_rt = [
    resize,
    resize
];

